let myLibrary = [];

// Book Constructor
function Book(title, author, imageUrl) {
  this.title = title;
  this.author = author;
  this.imageUrl = imageUrl;
  this.id = crypto.randomUUID(); 
  this.read = false; 
}


Book.prototype.toggleReadStatus = function() {
  this.read = !this.read;
};


const openButton = document.querySelector('.new-book');
const modal = document.querySelector(".modal");
const closeButton = document.querySelector('.close');
const form = document.querySelector('#newBookForm');
const bookSection = document.querySelector('.book-section');



// Open dialog box
openButton.addEventListener("click", () => {
  modal.style.display = "flex";
});

// Close dialog box
closeButton.addEventListener("click", () => {
  modal.style.display = "none";
  form.reset();
});


// Handle form submission to add new book
form.addEventListener("submit", function (e) {
  e.preventDefault(); 

  const title = document.querySelector("#bookTitle").value;
  const author = document.querySelector("#bookAuthor").value;
  const image = document.querySelector("#bookImage").value;
  const isRead = document.querySelector('.Completed').unchecked;


  const newBook = new Book(title, author, image); 
  newBook.read = isRead; 

  myLibrary.push(newBook); 

  form.reset(); 
  modal.style.display = "none"; 
  renderBooks(); 
});


bookSection.addEventListener('click', (event) => {
  const target = event.target; 

  // Handle Delete button click
  if (target.classList.contains('delete')) {
    const bookCard = target.closest('.book');
    if (bookCard && bookCard.dataset.id) { 
      const bookIdToDelete = bookCard.dataset.id;
      
      myLibrary = myLibrary.filter(book => book.id !== bookIdToDelete);
      renderBooks(); 
    } else {
      console.warn("Delete button clicked, but couldn't find book card or its ID.");
    }
  }
  if (target.classList.contains('Completed') || target.classList.contains('toggle-read')) {
      const bookCard = target.closest('.book');
      if (bookCard && bookCard.dataset.id) {
          const bookIdToToggle = bookCard.dataset.id;
          const bookToToggle = myLibrary.find(book => book.id === bookIdToToggle); // Find the book object

          if (bookToToggle) {
              bookToToggle.read = target.checked;
              }
              renderBooks(); 
          }
      }
  }
);


// Function to loop through the array and display each book on the page
function renderBooks() {
  bookSection.innerHTML = '';

  myLibrary.forEach(function(book) { 
    const divBook = document.createElement("div");
    divBook.classList.add("book");
    divBook.dataset.id = book.id; 

    const divImage = document.createElement('div');
    divImage.classList.add("image");
    const imageUrlElement = document.createElement("img");
    imageUrlElement.src = book.imageUrl;
    imageUrlElement.alt = `Cover of ${book.title}`; 
    divImage.append(imageUrlElement);
    divBook.append(divImage);

    const pAuthor = document.createElement('p');
    pAuthor.classList.add('author')
    pAuthor.textContent = `Author: ${book.author}`; 
    const pTitle = document.createElement('p');
    pTitle.classList.add('title')
    pTitle.textContent = `Title: ${book.title}`;
    divBook.append(pTitle);
    divBook.append(pAuthor);

    const divButton = document.createElement('div');
    divButton.classList.add('button');

    
    const inputCheckbox = document.createElement('input');
    inputCheckbox.classList.add('Completed'); 
    inputCheckbox.type = 'checkbox';
    inputCheckbox.checked = book.read;
    divButton.append(inputCheckbox);

    const pCompleted = document.createElement('p');
    pCompleted.textContent = 'Completed'; 
    divButton.append(pCompleted);

    const delButton = document.createElement('button');
    delButton.classList.add('delete'); 
    delButton.textContent = 'Delete Book'; 
    divButton.append(delButton);

    divBook.append(divButton); 

    bookSection.append(divBook); 
  });
}



// Add some initial books to myLibrary so something shows up on page load
myLibrary.push(new Book("The Hobbit", "J.R.R. Tolkien", "data:image/jpeg;base64,"));
myLibrary.push(new Book("1984", "George Orwell", "https://prodimage.images-bn.com/pimages/9780452262935_p0_v6_s600x595.jpg"));
myLibrary.push(new Book("Pride and Prejudice", "Jane Austen", "data:image/jpeg;base64,"));
myLibrary.push(new Book("The Catcher In The Rye", "J.D. Slinger", "data:image/png;base64,"));
myLibrary.push(new Book("Harry Potter And The Philosopher's Stone", "J.K. Rowling", "data:image/jpeg;base64,"));
myLibrary[0].read = true; 


renderBooks();
 